pragma solidity ^0.4.21;

contract Access {
    address public OGAdmin;
    mapping (address => bool) admin;
    mapping (address => mapping (bytes32 => bool)) public capabilities;

    constructor () public {
        OGAdmin = msg.sender;
        admin[msg.sender] = true;
    } 

    event CapabilityGranted(address user, bytes32 capability);

    event CapabilityRevoked(
        address user,
        bytes32 capability
    );    

    function grantAdminStatus(address newAdmin) public {
        if (admin[msg.sender] == true) {
            admin[newAdmin] = true;
        }
    }

    function revokeAdminStatus(address adminToRemove) public {
        if (msg.sender == OGAdmin) {
            admin[adminToRemove] = false;
        }
    }

    function grantCapability(address user, bytes32 capability) public {
        if(admin[msg.sender] == true) {
            capabilities[user][capability] = true;
            emit CapabilityGranted(user, capability);
        }
    }

    function revokeCapability(address user, bytes32 capability) public {
        if(admin[msg.sender] == true) {
            capabilities[user][capability] = false;
            emit CapabilityRevoked(user, capability);
        }
    }

    function isPermitted(address user, bytes32 capability) public constant returns (bool) {
        return capabilities[user][capability];
    }
}
