pragma solidity ^0.4.24;

contract PayDeposit {
    address public OGAdmin;
    mapping (address => bool) admin;
    mapping (address => bool) public paid;

    constructor () public {
        OGAdmin = msg.sender;
        admin[msg.sender] = true;
    }

    function grantAdminStatus(address newAdmin) public {
        if (admin[msg.sender] == true) {
            admin[newAdmin] = true;
        }
    }

    function revokeAdminStatus(address adminToRemove) public {
        if (msg.sender == OGAdmin) {
            admin[adminToRemove] = false;
        }
    }

    function payDeposit(address user) public {
        if(admin[msg.sender] == true) {
            paid[user] = true;
        }
    }

    function expireDeposit(address user) public {
        if(admin[msg.sender] == true) {
            paid[user] = false;
        }
    }

    function hasPaidDeposit(address user) public constant returns (bool) {
        return paid[user];
    }
}
